using namespace std;

int numWays(int n) {
    if (n < 2) return 0;

    vector<long long> dp(n + 1, 0);
    dp[0] = 1;
    dp[1] = 1;

    for (int i = 2; i <= n; i++) {
        dp[i] = dp[i - 1] + dp[i - 2];
    }

    long long result = 0;
    for (int i = 1; i <= n; i++) {
        int j = i;
        while (j <= n) {
            if ((n - i) % (j - i) == 0) {
                result += dp[(n - i) / (j - i)];
            }
            j += 1;
        }
    }

    return result;
}

int main() {
    cout << numWays(5) << endl; // Output: 2
    cout << numWays(9) << endl; // Output: 3
    cout << numWays(15) << endl; // Output: 4
    return 0;
}
